const mockGemini = {
  generateContent: async (prompt) => {
    // Mock Gemini responses
    const responses = {
      'tech fest': "Tech fests are excellent for networking with industry professionals, learning about cutting-edge technologies, and showcasing your skills through competitions. They often feature workshops, hackathons, and talks by leading tech experts.",
      'cultural event': "Cultural events help you explore creativity, meet diverse people, and develop soft skills like teamwork and communication. They're perfect for stress relief and discovering hidden talents.",
      'gdg': "Google Developer Groups (GDG) events provide hands-on experience with Google technologies, networking opportunities with developers, and insights into industry trends. Great for career development!",
      'workshop': "Workshops offer practical, hands-on learning experiences that complement your academic curriculum. They're perfect for gaining industry-relevant skills and building your portfolio."
    };
    
    const lowerPrompt = prompt.toLowerCase();
    for (const [key, response] of Object.entries(responses)) {
      if (lowerPrompt.includes(key)) {
        return { response: { text: () => response } };
      }
    }
    return { response: { text: () => "I'd be happy to help you learn more about campus events! Ask me about specific events, their benefits, or how to make the most of them." } };
  }
};
