import React, { useState } from 'react';
import firebase from '../services/firebase';
import './LoginForm.css';

const LoginForm = ({ onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);

    try {
      await firebase.auth.signInWithEmailAndPassword(email, password);
      onLogin();
    } catch (error) {
      console.error('Login failed:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const quickLogin = (userType) => {
    const email = userType === 'student' ? 'student@college.edu' : 'teacher@college.edu';
    setEmail(email);
    setPassword('password123');
  };

  return (
    <div className="login-container">
      <div className="login-card">
        <h2 className="login-title">Welcome to CampusSphere</h2>
        <p className="login-subtitle">AI-powered Campus Event Companion</p>

        <form onSubmit={handleSubmit}>
          <label htmlFor="email" className="input-label">Email address</label>
          <input
            id="email"
            type="email"
            required
            className="input-field"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <label htmlFor="password" className="input-label">Password</label>
          <input
            id="password"
            type="password"
            required
            className="input-field"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <div className="quick-login">
            <button
              type="button"
              className="quick-btn"
              onClick={() => quickLogin('student')}
            >
              Quick Login as Student
            </button>
            <button
              type="button"
              className="quick-btn"
              onClick={() => quickLogin('teacher')}
            >
              Quick Login as Teacher
            </button>
          </div>

          <button
            type="submit"
            disabled={isLoading}
            className="submit-btn"
          >
            {isLoading ? 'Logging in...' : 'Sign In'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default LoginForm;
